package main

import "C"

/*
#include "wchar.h"
*/

import (
	"fmt"
	"unsafe"

	wchar "github.com/GeertJohan/cgo.wchar"
)

func main() {
	for _, c := range wchars {
		wc := wchar.FromWcharPtr(unsafe.Pointer(&c))
		r, err := wc.GoRune()
		if err != nil {
			fmt.Printf("%#04x: ' ', // Error %v\n", c, err)
		} else {
			fmt.Printf("%#04x: %c,", c, r)
		}
	}
}

